mapper.update:
update user set
nickname = #{user.nickname},
user.avatar = #{user.avatar},
user.describe = #{user.describe}
where uid = #{user.uid}

update user set
username = #{user.username},
user.password = #{user.password}
where uid = #{user.uid}

update user set
avatar = #{avatarUrl}
where uid = #{uid}

mapper.delete:
delete from user_book
where
user_book.uid = #{uid} and user_book.book_id = #{bookId}
